/**
 * LivelyIcons CSS Animations
 *
 * Install via: npx shadcn@latest add lively-animations
 * Or copy this file to your project's styles directory.
 *
 * CSS Custom Properties:
 * --lively-icon-size: Icon size (default: 24px)
 * --lively-stroke-width: Stroke width (default: 2)
 * --lively-animation-duration: Base animation duration (default: 0.3s)
 */

/* CSS Custom Properties */
:root {
  --lively-icon-size: 24px;
  --lively-stroke-width: 2;
  --lively-animation-duration: 0.3s;
}

/* ========================
   Animation Keyframes
   ======================== */

/* Scale animation */
@keyframes lively-scale {
  0%,
  100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.15);
  }
}

/* Rotate animation */
@keyframes lively-rotate {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(180deg);
  }
}

/* Translate animation */
@keyframes lively-translate {
  0%,
  100% {
    transform: translate(0, 0);
  }
  50% {
    transform: translate(2px, -2px);
  }
}

/* Shake animation */
@keyframes lively-shake {
  0%,
  100% {
    transform: translateX(0);
  }
  20% {
    transform: translateX(-3px);
  }
  40% {
    transform: translateX(3px);
  }
  60% {
    transform: translateX(-3px);
  }
  80% {
    transform: translateX(3px);
  }
}

/* Pulse animation */
@keyframes lively-pulse {
  0%,
  100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.1);
    opacity: 0.8;
  }
}

/* Bounce animation */
@keyframes lively-bounce {
  0%,
  100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-6px);
  }
}

/* Draw animation (opacity/scale fallback) */
@keyframes lively-draw {
  0% {
    opacity: 0.4;
    transform: scale(0.9);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

/* Spin animation */
@keyframes lively-spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

/* Fade animation */
@keyframes lively-fade {
  0% {
    opacity: 0.6;
  }
  100% {
    opacity: 1;
  }
}

/* Pop animation */
@keyframes lively-pop {
  0%,
  100% {
    transform: scale(1) rotate(0deg);
  }
  50% {
    transform: scale(1.15) rotate(5deg);
  }
}

/* Wiggle animation */
@keyframes lively-wiggle {
  0%,
  100% {
    transform: rotate(0deg);
  }
  15% {
    transform: rotate(-12deg);
  }
  30% {
    transform: rotate(12deg);
  }
  45% {
    transform: rotate(-8deg);
  }
  60% {
    transform: rotate(8deg);
  }
  75% {
    transform: rotate(-4deg);
  }
  90% {
    transform: rotate(4deg);
  }
}

/* Heartbeat animation */
@keyframes lively-heartbeat {
  0%,
  100% {
    transform: scale(1);
  }
  14% {
    transform: scale(1.15);
  }
  28% {
    transform: scale(1);
  }
  42% {
    transform: scale(1.1);
  }
}

/* Swing animation */
@keyframes lively-swing {
  0%,
  100% {
    transform: rotate(0deg);
  }
  20% {
    transform: rotate(15deg);
  }
  40% {
    transform: rotate(-10deg);
  }
  60% {
    transform: rotate(5deg);
  }
  80% {
    transform: rotate(-5deg);
  }
}

/* Float animation */
@keyframes lively-float {
  0%,
  100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-8px);
  }
}

/* Path drawing animation for SVG strokes */
@keyframes lively-draw-path {
  0% {
    stroke-dashoffset: var(--path-length, 100);
    opacity: 0.3;
  }
  100% {
    stroke-dashoffset: 0;
    opacity: 1;
  }
}

/* Path drawing loop animation */
@keyframes lively-draw-path-loop {
  0% {
    stroke-dashoffset: var(--path-length, 100);
    opacity: 0.5;
  }
  40% {
    stroke-dashoffset: 0;
    opacity: 1;
  }
  60% {
    stroke-dashoffset: 0;
    opacity: 1;
  }
  100% {
    stroke-dashoffset: var(--path-length, 100);
    opacity: 0.5;
  }
}

/* ========================
   Animation Classes
   ======================== */

/* Base icon styling */
.lively-icon {
  display: inline-flex;
  width: var(--lively-icon-size, 24px);
  height: var(--lively-icon-size, 24px);
  transition: transform var(--lively-animation-duration) ease;
}

/* Scale on hover */
.lively-scale:hover {
  animation: lively-scale 0.3s ease forwards;
}

/* Rotate on hover */
.lively-rotate:hover {
  animation: lively-rotate 0.4s ease forwards;
}

/* Translate on hover */
.lively-translate:hover {
  animation: lively-translate 0.3s ease forwards;
}

/* Shake on hover */
.lively-shake:hover {
  animation: lively-shake 0.4s ease;
}

/* Pulse continuous */
.lively-pulse {
  animation: lively-pulse 0.6s ease-in-out infinite;
}

/* Bounce on hover */
.lively-bounce:hover {
  animation: lively-bounce 0.4s ease;
}

/* Draw on hover */
.lively-draw:hover {
  animation: lively-draw 0.5s ease forwards;
}

/* Spin continuous */
.lively-spin {
  animation: lively-spin 0.8s linear infinite;
}

/* Fade on hover */
.lively-fade {
  opacity: 0.6;
  transition: opacity 0.2s ease;
}
.lively-fade:hover {
  opacity: 1;
}

/* Pop on hover */
.lively-pop:hover {
  animation: lively-pop 0.3s ease forwards;
}

/* Wiggle on hover */
.lively-wiggle:hover {
  animation: lively-wiggle 0.6s ease;
}

/* Heartbeat on hover */
.lively-heartbeat:hover {
  animation: lively-heartbeat 0.8s ease;
}

/* Swing on hover */
.lively-swing:hover {
  animation: lively-swing 0.6s ease;
}

/* Float continuous */
.lively-float {
  animation: lively-float 1.5s ease-in-out infinite;
}

/* ========================
   Trigger Modes
   ======================== */

.lively-trigger-hover:hover {
  animation-play-state: running;
}

.lively-trigger-loop {
  animation-iteration-count: infinite;
}

.lively-trigger-mount {
  animation-iteration-count: 1;
  animation-fill-mode: forwards;
}

/* ========================
   SVG Path Drawing
   ======================== */

.lively-draw-path {
  stroke-dasharray: var(--path-length, 100);
  stroke-dashoffset: var(--path-length, 100);
}

.lively-draw-path-hover:hover .lively-draw-path {
  animation: lively-draw-path 1.5s ease-in-out forwards;
}

.lively-draw-path-loop .lively-draw-path {
  animation: lively-draw-path-loop 3s ease-in-out infinite;
}

.lively-draw-path-mount .lively-draw-path {
  animation: lively-draw-path 1.5s ease-in-out forwards;
}

/* ========================
   Reduced Motion Support
   ======================== */

@media (prefers-reduced-motion: reduce) {
  .lively-icon,
  .lively-scale,
  .lively-rotate,
  .lively-translate,
  .lively-shake,
  .lively-pulse,
  .lively-bounce,
  .lively-draw,
  .lively-spin,
  .lively-fade,
  .lively-pop,
  .lively-wiggle,
  .lively-heartbeat,
  .lively-swing,
  .lively-float {
    animation: none !important;
    transition: none !important;
  }
}
